import React, { useState, useEffect } from 'react';
import './App.css';

function ClientsPage() {
  const [clients, setClients] = useState([]);
  const [selectedClient, setSelectedClient] = useState(null);
  const [newClient, setNewClient] = useState({
    name: '',
    phoneNumber: '',
    tailNumber: '',
    additionalComments: ''
  });
  const [showModal, setShowModal] = useState(false);
  const [showClientDetailModal, setShowClientDetailModal] = useState(false);

  useEffect(() => {
    fetch('http://localhost:3001/api/clients')
      .then(response => response.json())
      .then(data => setClients(data))
      .catch(error => console.error('Error fetching clients:', error));

    const scrollContainer = document.querySelector('.scrollable-container');
    if (scrollContainer) {
      let isDragging = false;

      const onMouseDown = (e) => {
        // Only start drag on left click to avoid issues with middle or right clicks
        if (e.button !== 0) return; 
        isDragging = false; // Reset dragging flag on mouse down
        scrollContainer.style.cursor = 'grabbing';
        scrollContainer.classList.add('dragging'); // Optionally add a class to indicate dragging
      };

      const onMouseMove = () => {
        isDragging = true; // Set dragging flag on mouse move
      };

      const resetDragging = () => {
        isDragging = false;
        scrollContainer.style.cursor = 'grab';
        scrollContainer.classList.remove('dragging'); // Remove the dragging indicator class
      };

      scrollContainer.addEventListener('mousedown', onMouseDown);
      scrollContainer.addEventListener('mousemove', onMouseMove);

      // Handle mouse up on the window level to ensure it's captured outside the container
      window.addEventListener('mouseup', resetDragging);

      // Optionally, handle mouse leave on the scroll container
      scrollContainer.addEventListener('mouseleave', resetDragging);

      // Cleanup function to remove event listeners
      return () => {
        scrollContainer.removeEventListener('mousedown', onMouseDown);
        scrollContainer.removeEventListener('mousemove', onMouseMove);
        scrollContainer.removeEventListener('mouseleave', resetDragging);
        window.removeEventListener('mouseup', resetDragging);
      };
    }
  }, []);



  const toggleModal = () => {
    setShowModal(!showModal);
    document.body.classList.toggle('modal-open');
  };

  const toggleAddClientModal = () => {
    toggleModal();
  };

  const openClientDetailModal = (clientId) => {
    const client = clients.find(client => client.id.toString() === clientId);
    setSelectedClient(client);
    setShowClientDetailModal(true);
    document.body.classList.add('modal-open');
  };

  const closeModal = () => {
    setShowModal(false);
    setShowClientDetailModal(false);
    setSelectedClient(null);
    document.body.classList.remove('modal-open');
  };

  const handleAddClient = (e) => {
    e.preventDefault();
    fetch('http://localhost:3001/api/clients', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newClient),
    })
    .then(response => response.json())
    .then(data => {
      setClients([...clients, data]);
      setNewClient({ name: '', phoneNumber: '', tailNumber: '', additionalComments: '' });
      toggleModal();
    })
    .catch(error => console.error('Error:', error));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewClient({
      ...newClient,
      [name]: value
    });
  };

  return (
    <div className="main-content">
      {showModal && (
        <div className="modal-background" onClick={toggleModal}>
          <div className="modal-container" onClick={(e)=>e.stopPropagation()}>
            <div className="modal-content">
              <div className="add-client-form-section">
                <h3>Add New Client</h3>
                <form onSubmit={handleAddClient}>
                  <div className="form-group">
                    <label htmlFor="name">Name:</label>
                    <input type="text" id="name" name="name" placeholder="Name" value={newClient.name} onChange={handleInputChange} />
                  </div>
                  <div className="form-group">
                    <label htmlFor="phone-number">Phone Number:</label>
                    <input type="text" id="phone-number" name="phoneNumber" placeholder="Phone Number" value={newClient.phoneNumber} onChange={handleInputChange} />
                  </div>
                  <div className="form-group">
                    <label htmlFor="tail-number">Tail Number:</label>
                    <input type="text" id="tail-number" name="tailNumber"                     placeholder="Tail Number" value={newClient.tailNumber} onChange={handleInputChange} />
                  </div>
                  <div className="form-group">
                    <label htmlFor="additional-comments">Additional Comments:</label>
                    <textarea id="additional-comments" name="additionalComments" placeholder="Additional Comments" value={newClient.additionalComments} onChange={handleInputChange}></textarea>
                  </div>
                  <button type="submit" className="submit-client-btn">Add Client</button>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}

      {showClientDetailModal && selectedClient && (
        <div className="modal-background" onClick={closeModal}>
          <div className="modal-container" onClick={(e) => e.stopPropagation()}>
            <div className="modal-content">
              <h3>Client Details</h3>
              <p>Name: {selectedClient.name}</p>
              <p>Phone Number: {selectedClient.phoneNumber}</p>
              <p>Tail Number: {selectedClient.tailNumber}</p>
              <p>Additional Comments: {selectedClient.additionalComments}</p>
            </div>
          </div>
        </div>
      )}

      <div className="scrollable-container">
        <div className="card plus-card" onClick={toggleModal}>
          <img src="plus.png" alt="Add" width='100px' />
        </div>
        {clients.length > 0 ? (
          clients.map((client) => (
            <div key={client.id} className="card client-card" onClick={() => openClientDetailModal(client.id.toString())} data-client-id={client.id}>
              <div className="container">
                <h4><font size="60"><b>{client.name}</b></font></h4>
                <p>{client.tailNumber}</p>
              </div>
            </div>
          ))
        ) : (
          <div>Loading clients...</div>
        )}
      </div>
    </div>
  );
}

export default ClientsPage;
