import React, { useState, useEffect } from 'react';
import './App.css';
//import { Container, Panel } from 'react-scrolling-panel';
import { Panel, Placeholder } from 'rsuite';


function ClientsPage() {

  const [clients, setClients] = useState([]);
  const [selectedClient, setSelectedClient] = useState(null);
  const [newClient, setNewClient] = useState({
    name: '',
    phoneNumber: '',
    tailNumber: '',
    additionalComments: ''
  });
  const [showModal, setShowModal] = useState(false);
  const toggleModal = () => {
    setShowModal(!showModal);
    document.body.classList.toggle('modal-open');
  };
  // New state to control the visibility of the client detail modal
const [showClientDetailModal, setShowClientDetailModal] = useState(false);

// Function to toggle the add client modal
const toggleAddClientModal = () => {
  setShowModal(!showModal);
  document.body.classList.toggle('modal-open');
};

// Function to open the client detail modal
const openClientDetailModal = (client) => {
  setSelectedClient(client);
  setShowClientDetailModal(true);
  document.body.classList.add('modal-open');
};

// Function to close any modal and clear selected client
const closeModal = () => {
  setShowModal(false);
  setShowClientDetailModal(false);
  setSelectedClient(null);
  document.body.classList.remove('modal-open');
};

const [isDragging, setIsDragging] = useState(false);

  useEffect(() => {
    fetch('http://localhost:3001/api/clients')
      .then(response => response.json())
      .then(data => setClients(data))
      .catch(error => console.error('Error fetching clients:', error));
      const scrollContainer = document.querySelector('.scrollable-container');

    if (scrollContainer) { // Only proceed if the element exists
      let isDown = false;
      let startX;
      let scrollLeft;

      const startDrag = (e) => {
        if (e.button === 1) return; // Ignore middle mouse button
        isDown = true;
        setIsDragging(false); // Assume not dragging initially
        scrollContainer.style.cursor = 'grabbing';
        startX = e.pageX - scrollContainer.offsetLeft;
        scrollLeft = scrollContainer.scrollLeft;
      };

      const endDrag = () => {
        isDown = false;
        scrollContainer.style.cursor = 'grab';
        if (isDragging) {
          setIsDragging(false);
        }
      };

      const whileDragging = (e) => {
        if (!isDown) return;
        e.preventDefault();
        const x = e.pageX - scrollContainer.offsetLeft;
        const walk = (x - startX) * 3; // Adjust drag sensitivity if needed
        const isActuallyDragging = Math.abs(x - startX) > 5; // Threshold to consider as dragging
        setIsDragging(isActuallyDragging);

        scrollContainer.scrollLeft = scrollLeft - walk;
      };

      const handleClientClick = (client) => {
        if (!isDragging) {
          openClientDetailModal(client);
        }
      };
      scrollContainer.addEventListener('mousedown', startDrag);
      scrollContainer.addEventListener('mouseleave', endDrag);
      scrollContainer.addEventListener('mouseup', endDrag);
      scrollContainer.addEventListener('mousemove', whileDragging);



      scrollContainer.addEventListener('mousedown', (e) => {
        // Middle mouse button should be treated differently
        if (e.button === 1) return; // Ignore middle mouse button for dragging
        isDown = true;
        scrollContainer.style.cursor = 'grabbing';
        startX = e.pageX - scrollContainer.offsetLeft;
        scrollLeft = scrollContainer.scrollLeft;
      });

      scrollContainer.addEventListener('mouseleave', () => {
        isDown = false;
        scrollContainer.style.cursor = 'grab';
      });

      scrollContainer.addEventListener('mouseup', (e) => {
        if (e.button === 1) return; // Ignore middle mouse button for dragging
        isDown = false;
        scrollContainer.style.cursor = 'grab';
      });

      scrollContainer.addEventListener('mousemove', (e) => {
        if (!isDown) return;
        e.preventDefault();
        const x = e.pageX - scrollContainer.offsetLeft;
        const walk = (x - startX) * 1; // The number 2 here is the scroll speed
        scrollContainer.scrollLeft = scrollLeft - walk;
      });


    }
  }, []);



  const handleAddClient = (e) => {
    e.preventDefault();
    fetch('http://localhost:3001/api/clients', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newClient),
    })
    .then(response => response.json())
    .then(data => {
      setClients([...clients, data]);
      setNewClient({ name: '', phoneNumber: '', tailNumber: '', additionalComments: '' });
    })
    .catch(error => console.error('Error:', error));
  };
  const currentClients = ['Client 1', 'Client 2', 'Client 3'];
  function handleSelectChange(e) {
    const clientId = e.target.value;
    const client = clients.find(client => client.id === parseInt(clientId));
    setSelectedClient(client);
  }

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewClient({
      ...newClient,
      [name]: value
    });
  };


  return (

    <div className="main-content">
      {showModal && (
    <div className="modal-background" onClick={toggleModal}>
      <div className="modal-container" onClick={(e)=>e.stopPropagation()}>
        <div className="modal-content">
        <div className="add-client-form-section">
        <h3>Add New Client</h3>
        <form onSubmit={handleAddClient}>
          <div className="form-group">
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" name="name" placeholder="Name" value={newClient.name} onChange={handleInputChange} />
          </div>
          <div className="form-group">
            <label htmlFor="phone-number">Phone Number:</label>
            <input type="text" id="phone-number" name="phone_number" placeholder="Phone Number" value={newClient.phone_number} onChange={handleInputChange} />
          </div>
          <div className="form-group">
            <label htmlFor="tail-number">Tail Number:</label>
            <input type="text" id="tail-number" name="tail_number" placeholder="Tail Number" value={newClient.tail_number} onChange={handleInputChange} />
          </div>
          <div className="form-group">
            <label htmlFor="additional-comments">Additional Comments:</label>
            <textarea id="additional-comments" name="additional_comments" placeholder="Additional Comments" value={newClient.additional_comments} onChange={handleInputChange}></textarea>
          </div>
          <button type="submit" onClick={toggleModal} className="submit-client-btn">Add Client</button>
        </form>
      </div>
        </div>

      </div>
    </div>
  )}

{showClientDetailModal && selectedClient && (
  <div className="modal-background" onClick={closeModal}>
    <div className="modal-container" onClick={(e) => e.stopPropagation()}>
      <div className="modal-content">
        <h3>Client Details</h3>
        <p>Name: {selectedClient.name}</p>
        <p>Phone Number: {selectedClient.phoneNumber}</p> {/* Ensure correct property names */}
        <p>Tail Number: {selectedClient.tailNumber}</p>
        <p>Additional Comments: {selectedClient.additionalComments}</p>
      </div>
    </div>
  </div>
)}

    <div className="scrollable-container">
    <div className="card plus-card" onClick={toggleModal}>
    <img src="plus.png" alt="Add" width='100px' />
  </div>
  {clients.length > 0 ? (
    clients.map((client) => (
      <div key={client.id} className="card client-card" onClick={() => (handleClientClick(client), openClientDetailModal(client))}>
        <div className="container">
          <h4><b>{client.name}</b></h4>
          <p>{client.tailNumber}</p>
        </div>
      </div>
    ))
  ) : (
    <div>Loading clients...</div>
  )}
</div>  

    </div>



  );


}

export default ClientsPage;
