// InventoryPage.js
import React, { useState, useEffect } from 'react';
import './App.css';

function InventoryPage() {
  const [inventoryItems, setInventoryItems] = useState([]);
  const [showInventory, setShowInventory] = useState(false);
  const [itemName, setItemName] = useState('');
  const [itemQuantity, setItemQuantity] = useState('');
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetch('http://localhost:3001/api/inventory')
      .then(response => response.json())
      .then(data => {
        setInventoryItems(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching inventory:', error);
        setLoading(false);
      });
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:3001/api/inventory/search?searchQuery=${searchTerm}`);
      const data = await response.json();
      setInventoryItems(data);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  const handleAddItem = (e) => {
    e.preventDefault(); // Prevent form submission if you're using a form
    const newItem = {
      item_name: itemName,
      quantity: itemQuantity,
    };

    fetch('http://localhost:3001/api/inventory', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newItem),
    })
    .then(response => response.json())
    .then(data => {
      setInventoryItems(prevInventoryItems => [...prevInventoryItems, data]);
      setItemName('');
      setItemQuantity('');
      console.log('Item added:', data);
    })
    .catch(error => {
      console.error('Error adding item:', error);
    });
  };

  const confirmDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      deleteItem(id);
    }
  };

  const deleteItem = (id) => {
    fetch(`http://localhost:3001/api/inventory/${id}`, {
      method: 'DELETE',
    })
    .then(response => response.json())
    .then(() => {
      setInventoryItems(inventoryItems.filter(item => item.id !== id));
    })
    .catch(error => console.error('Error deleting item:', error));
  };

  if (loading) {
    return <div>Loading inventory...</div>;
  }

  return (
    <div className="main-content">
      <button className="inventory-button" onClick={() => setShowInventory(!showInventory)}>
        {showInventory ? 'Hide Inventory' : 'Show Inventory'}
      </button>

      {showInventory && (
        <section className="inventory-section">
          <h2>Inventory</h2>
          <ul>
            {inventoryItems.map(item => (
              <li key={item.id}>
                {item.item_name} - Quantity: {item.quantity}
                <button onClick={() => confirmDelete(item.id)} className="delete-button">
                  X
                </button>
              </li>
            ))}
          </ul>
        </section>
      )}

      <section className="add-inventory-section">
        <h2>Add Inventory Item</h2>
        <form onSubmit={handleAddItem}>
          <input 
            type="text" 
            placeholder="Item Name" 
            value={itemName} 
            onChange={e => setItemName(e.target.value)}
          />
          <input 
            type="number" 
            placeholder="Quantity" 
            value={itemQuantity} 
            onChange={e => setItemQuantity(e.target.value)}
          />
          <button type="submit">Add Item</button>
        </form>
      </section>

      <section className="search-section">
        <h2>Search Inventory</h2>
        <form onSubmit={handleSearch}>
          <input 
            type="text" 
            placeholder="Search..." 
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
        {/* Display search results */}
      </section>
    </div>
  );
}

export default InventoryPage;
