import React, { useState } from 'react';
import './App.css';

function App() {
  const [showInventory, setShowInventory] = useState(false);
  // State to store the input values
  const [itemName, setItemName] = useState('');
  const [itemQuantity, setItemQuantity] = useState('');

  // Function to handle adding an item (logic to be implemented)
  const handleAddItem = () => {
    console.log("Adding item:", itemName, "Quantity:", itemQuantity);
    // Add logic to actually add the item to inventory
  }
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Function to toggle the menu
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={"jetcorrect.png"} alt="Logo" className="App-logo" /> {}
        <button onClick={toggleMenu} className="menu-button">Menu</button>
      </header>
      <div className={`sidebar ${isMenuOpen ? 'open' : ''}`}>
        <button onClick={() => {/* navigate to Inventory */}}>Inventory</button>
        <button onClick={() => {/* navigate to Clients */}}>Clients</button>
      </div>
      <button className="inventory-button" onClick={() => setShowInventory(!showInventory)}>
        {showInventory ? 'Hide Inventory' : 'Show Inventory'}
      </button>
      {showInventory && (
        <section className="inventory-section">
          <h2>Inventory</h2>
          {/* Inventory items will be listed here */}
        </section>
      )}
      <section className="add-inventory-section">
        <h2>Add Inventory Item</h2>
        <input 
          type="text" 
          placeholder="Item Name" 
          value={itemName} 
          onChange={e => setItemName(e.target.value)}
        />
        <input 
          type="number" 
          placeholder="Quantity" 
          value={itemQuantity} 
          onChange={e => setItemQuantity(e.target.value)}
        />
        <button onClick={handleAddItem}>Add Item</button>
      </section>
      <section className="search-section">
        <h2>Search Inventory</h2>
        <input type="text" placeholder="Search..."/>
        <button>Search</button>
        {/* Search functionality will be implemented here */}
      </section>
      <footer className="App-footer">
        <p>Â© 2023 JetCorrect</p>
      </footer>
    </div>
  );
}

export default App;
